const webcam = function() {
  const video = document.createElement('video');
  const canvas = document.createElement('canvas');
  const context = canvas.getContext('2d');

  // Create a new webcam object
  const webcam = new WebRTCPeerConnection();

  // Define the webcam video stream
  const stream = webcam.getVideoTracks().map(track => track);

  // Create a new video stream
  const videoStream = stream.getVideoTracks();

  // Add the video stream to the canvas
  videoStream.forEach(track => {
    video.srcObject = track;
  });

  // Create a new object for the webcam
  const webcamObject = {
    video: video,
    canvas: canvas,
    context: context,
    stream: stream,
  };

  return webcamObject;
}

