    scanf("%f", &distance);

    printf("Enter the time taken in seconds: ");
    scanf("%f", &time);

    // Calculate speed
    if (time > 0) { // Avoid division by zero
        speed = distance / time;
    } else {
        printf("Error: Time cannot be zero.\n");
        return 1; // Exit with error code
    }

    // Print the result
    printf("The speed of the object is %.2f meters per second.\n", speed);

